mapper.insert:
INSERT INTO PRODUCT(
	  PRODUCT_ID
	, PNAME
	, UNIT_PRICE
	, DESCRIPTION
	, MANUFACTURER
	, CATEGORY
	, UNITS_IN_STOCK
	, CONDITION
	 ) 
VALUES(#{productId}
	, #{pname}
	, #{unitPrice}
	, #{description}
	, #{manufacturer}
	, #{category}
	, #{unitsInStock}
	, #{condition}

	)

mapper.update:
UPDATE	PRODUCT
SET	 PNAME=#{pname}, UNIT_PRICE=#{unitPrice}, DESCRIPTION=#{description}, MANUFACTURER=#{manufacturer}
		 , CATEGORY=#{category}, UNITS_IN_STOCK=#{unitInStock}, CONDITION=#{condition}
WHERE PRODUCT_ID = #{productId}
